if [ ! -d "./node_modules" ]; then echo "Dependencies not found. Installing..."; npm install; else echo "Dependencies found. Skipping installation."; fi; if [ ! -d "./logs" ]; then echo "Logs directory not found. Creating..."; mkdir logs; fi; REQUIRED_NODE_VERSION="21"; CURRENT_NODE_VERSION=$(node -v | cut -d '.' -f 1 | sed 's/^v//'); if [ "$CURRENT_NODE_VERSION" != "$REQUIRED_NODE_VERSION" ]; then echo "Node.js version $CURRENT_NODE_VERSION is not supported. Please install Node.js $REQUIRED_NODE_VERSION."; exit 1; else echo "Node.js version $CURRENT_NODE_VERSION is compatible."; fi; echo "Starting NetherCore..."; FORCE_COLOR=1 node ./source/index.js | tee -a ./logs/bot.log